import React, {useEffect} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { loginClassDispatched } from '../loginPage/loginSlice';


const Login = () => {

  const dispatch = useDispatch()
  const loginClass = useSelector(state => state.loginClass)


    // close login page on esc button


  return (
      <div className={loginClass.value ? 'showLogin' : 'hideLogin'} >
        <div>
           <h2>Login</h2>
           <button className='loginCloseBtn'  > close x </button>
        </div>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email' />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' />
            </div>
            <button type='submit' >
                Login
            </button>
        </form>

      </div>
  )
}

export default Login
