import React from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { signupClassDispatched } from './signupClassSlice';


const Signup = () => {

  const dispatch = useDispatch()
  const signupClass = useSelector(state => state.signupClass)

  // close signup page

  const closeSignup = () => {
    if (signupClass.value !== false) {
      dispatch(
        signupClassDispatched(false)
      )
    }
  }
    // input states
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const onEmailChange = (e) => setEmail(e.target.value)
  const onPasswordChange = (e) => setPassword(e.target.value)


  return (
      <div className={signupClass.value ? 'showSignup' : 'hideSignup'} >
           <h2>Sign-up</h2>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email' />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' />
            </div>
            <button className='signupSubmitButton' type='submit' >
                Sign-up
            </button>
        </form>
        <button className='signCloseButton' onClick={closeSignup} >
          Close
        </button>

      </div>
  )
}

export default Signup
