import React, {useState} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { loginClassDispatched } from './loginSlice';
import { useGetUsersQuery } from '../api/apiSlice';


const Login = () => {

  const dispatch = useDispatch()
  const loginClass = useSelector(state => state.loginClass)

  // close login page

  const closeLogin = () => {
    if (loginClass.value !== false) {
      dispatch(
        loginClassDispatched(false)
      )
    }
  }

  // input states
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const onEmailChange = (e) => setEmail(e.target.value)
  const onPasswordChange = (e) => setPassword(e.target.value)

  // check user data
  const {
    data: users,
    isLoading,
    isSuccess,
    isError,
    error
  } = useGetUsersQuery()
  const canSave = [email, password].every(Boolean) && !isLoading

  const onLogin =  () => {

    if (canSave) {
      const userData = users.find()
    }
    // let content

    // if (isLoading) {                    //loading
    //   content = <h2>...loading</h2>
    // } else if (isSuccess) {
    //   content = <h2>Success</h2>           // render posts on success
    // } else if (isError) {             // on error
    //   content = <h2 >{...error.toString()}</h2>
    //   console.log(users)
    // }
  }


  return (
      <div className={loginClass.value ? 'showLogin' : 'hideLogin'} >
           <h2>Login</h2>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email' onChange={onEmailChange} value={email} />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' onChange={onPasswordChange} value={password} />
            </div>
            <button className='loginSubmitButton' type='button' onClick={onLogin} >
                Login
            </button>
        </form>
        <button className='loginCloseButton' onClick={closeLogin} >
          Close
        </button>

      </div>
  )
}

export default Login
