import React from 'react'
import { useSelector, useDispatch } from 'react-redux';

const Login = () => {

  const loginClass = useSelector(state => state.loginClass)

    // close login page on esc button
    useEffect(() => {
      const handleEsc = (event) => {
         if (event.keyCode === 27) {
          if (loginClass.value !== false) {
            dispatch(
              loginClassDispatched({
                  value: false
              })
          )
          }
        }
      };
      window.addEventListener('keydown', handleEsc);

      return () => {
        window.removeEventListener('keydown', handleEsc);
      };
    }, []);
  return (
      <div className={loginClass.value ? 'showLogin' : 'hideLogin'} >
        <div>
           <h2>Login</h2>
           <button className='loginCloseBtn' onClick={closeAddPage} > close x </button>
        </div>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email' />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' />
            </div>
            <button type='submit' >
                Login
            </button>
        </form>

      </div>
  )
}

export default Login
