import React, {useState} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { loginClassDispatched } from './loginSlice';


const Login = () => {

  const dispatch = useDispatch()
  const loginClass = useSelector(state => state.loginClass)

  // close login page

  const closeLogin = () => {
    if (loginClass.value !== false) {
      dispatch(
        loginClassDispatched(false)
      )
    }
  }

  // input states
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const onEmailChange = (e) => setEmail(e.target.value)
  const onPasswordChange = (e) => setPassword(e.target.value)

  // check user data
  const canLogin = [email, password].every(Boolean) && !isLoading

  const onLogin = () => {
    if (canLogin) {
      if (canLogin) {
        const userData = users.find(user => user.email === email)
        console.log(userData)
        if (userData) {
          if (userData.password === password) {
            alert('success')
          } else alert('incorrect password')
        } else alert('incorrect email')
      } else alert('please enter email and password')
    }

  }


  return (
      <div className={loginClass.value ? 'showLogin' : 'hideLogin'} >
           <h2>Login</h2>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email' onChange={onEmailChange} value={email} />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' onChange={onPasswordChange} value={password} />
            </div>
            <button className='loginSubmitButton' type='button' onClick={onLogin} >
                Login
            </button>
        </form>
        <button className='loginCloseButton' onClick={closeLogin} >
          Close
        </button>

      </div>
  )
}

export default Login
