import React, {useEffect} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { loginClassDispatched } from '../loginPage/loginSlice';


const Login = () => {

  const dispatch = useDispatch()
  const loginClass = useSelector(state => state.loginClass)

  const closeLogin = () => {
    if (loginClass.value !== false) {
      dispatch(
        loginClassDispatched(false)
      )
    }
  }

  useEffect(() => {
    const handleEsc = (event) => {
       if (event.keyCode === 27) {
        if (loginClass.value !== false) {
          dispatch(
            loginClassDispatched(false)
          )

        }

      }
    };
    window.addEventListener('keydown', handleEsc);

    return () => {
      window.removeEventListener('keydown', handleEsc);
    };
  }, []);


  return (
      <div className={loginClass.value ? 'showLogin' : 'hideLogin'} >
           <h2>Login</h2>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email' />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' />
            </div>
            <button type='submit' >
                Login
            </button>
        </form>
        <button onClick={closeLogin} >
          Close
        </button>

      </div>
  )
}

export default Login
