import React, {useState} from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { signupClassDispatched } from './signupClassSlice';
import { useAddNewUserMutation } from '../api/apiSlice';


const Signup = () => {

  const dispatch = useDispatch()
  const signupClass = useSelector(state => state.signupClass)

  // close signup page

  const closeSignup = () => {
    if (signupClass.value !== false) {
      dispatch(
        signupClassDispatched(false)
      )
    }
  }
    // input states
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const onEmailChange = (e) => setEmail(e.target.value)
  const onPasswordChange = (e) => setPassword(e.target.value)

  // add new user
  const [addNewUser, { isLoading }] = useAddNewUserMutation()
  const canSave = [email, password].every(Boolean) && !isLoading

  // const onFormSubmit = async (e) => {
  //   if (canSave) {
  //     try {
  //       await addNewUser({  email,  password })
  //       setEmail('')
  //       setPassword('')
  //       dispatch(
  //         signupClassDispatched(false)
  //       )
  //     } catch (err) {
  //       console.error('fail', err)
  //     }
  //   } else alert('please enter email and password')
  // }


  return (
      <div className={signupClass.value ? 'showSignup' : 'hideSignup'} >
           <h2>Sign-up</h2>
        {/* form  */}
        <form >
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" name='email'  onChange={onEmailChange} value={email} />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" name='password' onChange={onPasswordChange} value={password} />
          </div>
            <button className='signupSubmitButton' type='button'  >
                Sign-up
            </button>
        </form>
        <button className='signupCloseButton' onClick={closeSignup} >
          Close
        </button>

      </div>
  )
}

export default Signup
