import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

export const apiSlice = createApi({
    reducerPath: 'api',
    baseQuery: fetchBaseQuery({ baseUrl: 'http://localhost:5000/' }),
    tagTypes: ['Post'],
    endpoints: builder => ({
        getPosts: builder.query({
            query: () => '/post',
            providesTags: ['Post']
        }),
        addNewPost: builder.mutation({
            query: initialPost => ({
                url: '/post',
                method: 'POST',
                body: initialPost
            }),
            invalidatesTags: ['Post']
        }),
        deletePostConfirm: builder.mutation({
            query: post => ({
                url: '/post',
                method: 'DELETE',
                body: post
            })
        })
    })
})

export const { useGetPostsQuery, useAddNewPostMutation, useDeletePostConfirmMutation } = apiSlice
